import './App.css';

import { useLayoutEffect, useState } from "react";

function App() {

  let isFileSelected = false

  function parseCSR() {
    console.log(fileContent)
  }

  function handleFileChange(event) {
    let file = event.target.files[0];
        if (file) {
          console.log(file)
          isFileSelected = true
        }
  }

  function handleContentChange(event) {
    setFileContent(event.target.value)
  }

  const [ fileContent, setFileContent ] = useState("")


  const axios = require('axios');


  return (
    <div className="App">
      <header className="App-header">
        <h1>DT-Sec Programmieraufgabe</h1>
        <h4>PKCS #10 CSR Parser</h4>
      </header>
      <div className="App-form-container">
        {/* Form für die Datei Eingabe */}
        <form onSubmit={parseCSR}>
          <label>Wähle ein CSR aus:</label>
          <input type="file" className='inputBox' onChange={handleFileChange} />
          <input type="submit" value="Datei parsen" />
        </form>
        <hr />
        <form onSubmit={parseCSR}>
          <label>Kopiere den Inhalt in folgende Box:</label>
          <textarea className='inputBox' name="CSR content" cols="1" rows="5" onChange={handleContentChange} value={fileContent} placeholder='-----BEGIN CERTIFICATE REQUEST-----...' disabled={isFileSelected}></textarea>
          <input type="submit" value="Text parsen" />
        </form>
      </div>
      {/* Hier steht das Ergebnis */}
      <div className='App-result-container'>
        <h1>Ergebnis:</h1>
      </div>
    </div>
  );
}

export default App;
