import './App.css';

import { useState } from "react";

function App() {

  const [ fileUrl, setFileUrl ] = useState("")
  const [ fileContent, setFileContent ] = useState("")

  return (
    <div className="App">
      <header className="App-header">
        <h1>DT-Sec Programmieraufgabe</h1>
      </header>
      <div class="App-form-container">
        {/* Form für die Datei Eingabe */}
        <form>
          <label>Wähle ein CSR aus:</label>
          <input type="file" onChange={(file) => setFileUrl(file)} value={fileUrl} />
          <hr />
          <label>oder kopiere den Inhalt in folgende Box:</label>
          <textarea name="CSR content" cols="40" rows="5" onChange={(text) => setFileContent(text)} value={fileContent}></textarea>
          <input type="submit" value="Submit" />
        </form>
      </div>
    </div>
  );
}

export default App;
